        useEffect(() => {
            fetch('https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid=2dd4f7656d66a8ff5ac6d70242d6690e', 
            {
            }).then(res=>res.json())
            .then(result=>{
                console.log(result.main.temp - 273.15)
