                {
                    setImgname('mild')
                }


                if ((result.main.temp - 273.15) <= 10)
                {
                    setImgname('cold')
                }

            })
        },[])




    return (
      <div className="App">
        <img src= {cold} alt="PHC"></img>

        <div>
            <button>
                "Change to °F"
            </button>

            <button>
                "Change to °C"
            </button>
        </div>

      </div>
    );
  }

  let toCelcius= (fahrenheit) =>{
    return (fahrenheit - 32) * (5/9);
  }

  let toFahrenheit= (celcius) =>{
    return (celcius * (9/5)) + 32;
  }

  export default Weather;
