const Weather= () => {
    const [data, setData] = useState();
    const [image, setImage] = useState(sunny)


        //Sammerr

        useEffect(() => {
            fetch('https://api.openweathermap.org/data/2.5/weather?lat=4.824167&lon=7.033611&appid=2dd4f7656d66a8ff5ac6d70242d6690e', 
            {
            }).then(res=>res.json())
            .then(result=>{
                console.log(result.main.temp - 273.15)
                setData(result.main.temp - 273.15)

                if(data >= 20)
                {
                    console.log("I was here")
                    setImage(sunny)
                }

                if(data > 10 & data <= 20)
                {
                    setImage(mild)
                }

                if(data <= 10)
                {
                    setImage(cold)
                }

            })
        },[])




    return (
      <div className="App">
        <h1>{data}</h1>
        <img src= {image} alt="cold"></img>

        <div>
            <button>
                "Change to Â°F"
            </button>

        </div>

      </div>
    );
  }

  let toCelcius= (fahrenheit) =>{
    return (fahrenheit - 32) * (5/9);
  }

  let toFahrenheit= (celcius) =>{
    return (celcius * (9/5)) + 32;
  }

  export default Weather;
