import React from 'react';

import { useState, useEffect } from "react";

import cold from "../images/cold.png";
import sunny from "../images/sunny.png";
import mild from "../images/mild.png";


const Weather= () => {
    const lat = 4.824167;
    const lon = 7.033611;
    let isCelcius = true;


    const [data, setData] = useState([]);

      const fetchInfo = () => { 
        return 
            fetch('https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={9c89f48be4912d6bf1e1108a4c1fd844') 
                .then((res) => res.json()) 
                .then((d) => setData(d.main.temp));
                if(toCelcius(d) > 20)
                    console.log("It is sunny");
                if(toCelcius(d) < 10 )
                    console.log("It is cold");
                if(toCelcius(d) >= 10 && toCelcius(d) <= 20)
                    console.log("It is mild");
        }

        useEffect(() => {
            fetchInfo();
        }, [])




    return (
      <div className="App">
        <img src= {cold} alt="PHC"></img>

        <div>
            <button>
                "Change to °F"
            </button>

            <button>
                "Change to °C"
            </button>
        </div>

      </div>
    );
  }

  let toCelcius= (fahrenheit) =>{
    return (fahrenheit - 32) * (5/9);
  }

  let toFahrenheit= (celcius) =>{
    return (celcius * (9/5)) + 32;
  }

  export default Weather;
